(function() { var dailyCTA = document.getElementById('branch-daily-prayer-1'); if (dailyCTA && typeof window.trackEvent !== 'undefined') { var link = dailyCTA.querySelector('a'); if (link) { link.addEventListener('click', function() { var eventData = { event_category: 'conversion', novena: "St. John the Apostle Novena", day: '1', novena_slug: 'st-john-the-apostle-novena', source: 'daily_prayer_cta' }; if (window.abTestDownloadFunnel) { eventData.ab_test = window.abTestDownloadFunnel.testName; eventData.ab_variant = window.abTestDownloadFunnel.variant; } window.trackEvent('daily_prayer_cta_clicked', eventData); }); } } })();

