(function() { var day = '8'; var pageUrl = 'https://catholicnovenaapp.com/novenas/st-jude-novena/'; var pageTitle = "St. Jude Novena"; var fbBtn = document.getElementById('share-facebook-' + day); var nativeBtn = document.getElementById('share-native-' + day); var copyBtn = document.getElementById('share-copy-' + day); var copyFeedback = document.getElementById('copy-feedback-' + day); function trackShare(platform) { if (typeof window.trackEvent !== 'undefined') { window.trackEvent('social_share_clicked', { event_category: 'engagement', novena: pageTitle, day: day, platform: platform, source: 'daily_prayer_share' }); } } if (fbBtn) { fbBtn.addEventListener('click', function() { trackShare('facebook'); var fbUrl = 'https://www.facebook.com/sharer/sharer.php?u=' + encodeURIComponent(pageUrl) + '&quote=' + encodeURIComponent(pageTitle); var left = (screen.width - 600) / 2; var top = (screen.height - 400) / 2; window.open(fbUrl, 'facebook-share', 'width=600,height=400,left=' + left + ',top=' + top); }); } if (nativeBtn && navigator.share) { nativeBtn.style.display = 'flex'; nativeBtn.addEventListener('click', function() { trackShare('native'); navigator.share({ title: pageTitle, url: pageUrl }); }); } if (copyBtn) { copyBtn.addEventListener('click', function() { trackShare('copy'); if (navigator.clipboard && navigator.clipboard.writeText) { navigator.clipboard.writeText(pageUrl).then(function() { showCopyFeedback(); }); } else { var textArea = document.createElement('textarea'); textArea.value = pageUrl; textArea.style.position = 'fixed'; textArea.style.left = '-9999px'; document.body.appendChild(textArea); textArea.select(); document.execCommand('copy'); document.body.removeChild(textArea); showCopyFeedback(); } }); } function showCopyFeedback() { if (copyFeedback) { copyFeedback.style.display = 'block'; setTimeout(function() { copyFeedback.style.display = 'none'; }, 2000); } } })();
